import React from 'react';
import { Box, Typography, CircularProgress, Grid } from '@mui/material';

const TaskCompletion = ({ taskCompletions }) => {
  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Assignment Completion by Area
        </Typography>
        <Typography variant="body1">No assignment data available.</Typography>
      </Box>
    );
  }

  // Group tasks by area and calculate progress
  const areaGroups = taskCompletions.reduce((acc, task) => {
    const areaName = task.area_name;

    if (!acc[areaName]) {
      acc[areaName] = { name: areaName, completed: 0, total: 0 };
    }

    acc[areaName].total += 1;
    if (task.is_correct) {
      acc[areaName].completed += 1;
    }

    return acc;
  }, {});

  const areas = Object.values(areaGroups);

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Assignment Completion by Area
      </Typography>
      <Grid container spacing={3}>
        {areas.map((area) => {
          const progress = area.total > 0 ? (area.completed / area.total) * 100 : 0;

          return (
            <Grid item xs={12} sm={6} md={4} key={area.name}>
              <Box
                display="flex"
                flexDirection="column"
                alignItems="center"
                justifyContent="center"
                sx={{
                  p: 3,
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                  height: '200px',
                }}
              >
                <Box position="relative" display="inline-flex">
                  <CircularProgress
                    variant="determinate"
                    value={progress}
                    size={100}
                    thickness={5}
                    sx={{ color: '#2196f3' }}
                  />
                  <Box
                    position="absolute"
                    top="50%"
                    left="50%"
                    transform="translate(-50%, -50%)"
                  >
                    <Typography variant="h6" sx={{ color: '#1565c0' }}>
                      {area.completed} / {area.total}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="h6" sx={{ color: '#1565c0', textAlign: 'center', mt: 2 }}>
                  {area.name}
                </Typography>
              </Box>
            </Grid>
          );
        })}
      </Grid>
    </Box>
  );
};

export default TaskCompletion;
