import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2', // Blue
    },
    secondary: {
      main: '#dc004e', // Red
    },
    chartColors: [
      '#0088FE', // Blue
      '#00C49F', // Teal
      '#FFBB28', // Yellow
      '#FF8042', // Orange
      '#8884d8', // Purple
    ],
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
});

export default theme;
