import React, { useEffect, useState } from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  PieChart, 
  Pie, 
  Cell, 
  LineChart, 
  Line 
} from 'recharts';
import { 
  Grid, 
  Paper, 
  Typography, 
  CircularProgress,
  Box 
} from '@mui/material';

const CHART_COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

const TaskAnalytics = ({ username }) => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const API_BASE_URL = 'http://localhost:5000/api';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/users/${username}/task-analytics`);
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();

        // Validate response structure
        if (!data) {
          throw new Error('Invalid data format from server');
        }

        setAnalyticsData(data);
        setError(null);
      } catch (error) {
        console.error('Fetch error:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" mt={4}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box mt={4} p={2} bgcolor="error.light" color="error.contrastText">
        <Typography variant="body1">Error loading analytics: {error}</Typography>
      </Box>
    );
  }
  console.log(analyticsData)
  if (!analyticsData) {
    return (
      <Box mt={4}>
        <Typography variant="body1">No analytics data available</Typography>
      </Box>
    );
  }
  return (
    <Grid container spacing={3} sx={{ mt: 2 }}>
      {/* Processing Time Trend */}
      <Grid item xs={12}>
        <Paper sx={{ p: 2 }}>
          <Typography variant="h6" gutterBottom>
            Total Processing Time by Area (seconds)
          </Typography>
          {analyticsData && analyticsData.length > 0 ? (
            <BarChart 
              width={800} 
              height={300} 
              data={analyticsData}
              margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="area_name" 
                label={{ 
                  value: 'Task Area', 
                  position: 'bottom' 
                }} 
              />
              <YAxis 
                label={{ 
                  value: 'Seconds', 
                  angle: -90, 
                  position: 'left' 
                }} 
              />
              <Tooltip formatter={(value) => [`${value}s`, 'Total Time']} />
              <Legend />
              <Bar dataKey="total_time" name="Total Time" fill="#8884d8" />
            </BarChart>
          ) : (
            <Box height={300} display="flex" alignItems="center" justifyContent="center">
              <Typography variant="body2">No processing time data available</Typography>
            </Box>
          )}
        </Paper>
      </Grid>
    </Grid>
  );  
};

export default TaskAnalytics;
