import React, { useState } from 'react';
import { 
  Box, Typography, LinearProgress, Grid, Collapse, Table, TableBody, TableCell, 
  TableContainer, TableHead, TableRow, Paper, IconButton 
} from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import CheckIcon from '@mui/icons-material/Check';
import CloseIcon from '@mui/icons-material/Close';

const TaskCompletion = ({ taskCompletions }) => {
  const [expandedAreas, setExpandedAreas] = useState({});

  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Assignment Completion by Area
        </Typography>
        <Typography variant="body1">No assignment data available.</Typography>
      </Box>
    );
  }

  // Sort tasks by ascending order of area_id
  const sortedTaskCompletions = [...taskCompletions].sort((a, b) => a.area_id - b.area_id);

  // Group tasks by area
  const tasksByArea = sortedTaskCompletions.reduce((acc, task) => {
    const areaName = task.area_name;

    if (!acc[areaName]) {
      acc[areaName] = {
        name: areaName,
        completed: 0,
        total: 0,
        tasks: []
      };
    }

    acc[areaName].total += 1;
    if (task.is_correct) {
      acc[areaName].completed += 1;
    }

    acc[areaName].tasks.push({
      id: task.statement_id,
      name: task.statement_text,
      isCompleted: task.is_correct,
      difficulty: task.difficulty || 'Medium'
    });

    return acc;
  }, {});

  const areas = Object.values(tasksByArea);

  const toggleAreaExpansion = (areaName) => {
    setExpandedAreas((prev) => ({
      ...prev,
      [areaName]: !prev[areaName]
    }));
  };

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Assignment Completion by Area
      </Typography>
      <Grid container spacing={3} direction="column">
        {areas.map((area) => {
          const progress = area.total > 0 ? (area.completed / area.total) * 100 : 0;

          return (
            <Grid item xs={12} key={area.name}>
              <Box
                sx={{
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                  overflow: 'hidden',
                }}
              >
                {/* Main area display */}
                <Box
                  sx={{
                    p: 3,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    justifyContent: 'center',
                    position: 'relative',
                  }}
                >
                  <Typography variant="h6" sx={{ color: '#1565c0', mb: 1 }}>
                    {area.name}
                  </Typography>
                  <LinearProgress
                    variant="determinate"
                    value={progress}
                    sx={{
                      width: '100%',
                      height: '10px',
                      borderRadius: '5px',
                      backgroundColor: '#bbdefb',
                      '& .MuiLinearProgress-bar': {
                        backgroundColor: '#2196f3',
                      },
                    }}
                  />
                  <Typography variant="body2" sx={{ color: '#1565c0', mt: 1 }}>
                    {area.completed} / {area.total} tasks completed
                  </Typography>
                  {/* Dropdown button */}
                  <IconButton 
                    size="small" 
                    sx={{ position: 'absolute', top: 8, right: 8 }}
                    onClick={() => toggleAreaExpansion(area.name)}
                  >
                    {expandedAreas[area.name] ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                  </IconButton>
                </Box>

                {/* Dropdown content */}
                <Collapse in={expandedAreas[area.name]}>
                  <TableContainer component={Paper}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell>Task</TableCell>
                          <TableCell>Status</TableCell>
                          <TableCell>Difficulty</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {area.tasks.map((task) => (
                          <TableRow key={task.id}>
                            <TableCell>{task.name}</TableCell>
                            {/* Use arrow or "X" for status */}
                            <TableCell>
                              {task.isCompleted ? (
                                <CheckIcon sx={{ color: 'green' }} />
                              ) : (
                                <CloseIcon sx={{ color: 'red' }} />
                              )}
                            </TableCell>
                            {/* Difficulty only for completed tasks */}
                            <TableCell>{task.isCompleted ? task.difficulty : '-'}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Collapse>
              </Box>
            </Grid>
          );
        })}
      </Grid>
    </Box>
  );
};

export default TaskCompletion;
