import React from 'react';
import { Box, Typography, CircularProgress, Button, Menu, MenuItem, Grid } from '@mui/material';

const TaskCompletion = ({ taskCompletions }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleDropdownOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleDropdownClose = () => {
    setAnchorEl(null);
  };

  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Task Completions
        </Typography>
        <Typography variant="body1">No task completion data available.</Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Task Completions by Area
      </Typography>
      <Grid container spacing={3}>
        {taskCompletions.map((area) => {
          const progress = (area.completed_count / area.total_tasks) * 100;

          return (
            <Grid item xs={12} sm={6} md={4} key={area.area_name}>
              <Box
                display="flex"
                flexDirection="column"
                alignItems="center"
                justifyContent="center"
                sx={{
                  p: 3,
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                }}
              >
                <CircularProgress
                  variant="determinate"
                  value={progress}
                  size={80}
                  thickness={5}
                  sx={{ color: '#2196f3', mb: 2 }}
                />
                <Typography variant="h6" sx={{ color: '#1565c0' }}>
                  {area.area_name}
                </Typography>
                <Typography variant="body2" sx={{ color: '#1565c0' }}>
                  {area.completed_count} / {area.total_tasks} tasks completed
                </Typography>
                <Button
                  variant="contained"
                  onClick={handleDropdownOpen}
                  sx={{
                    mt: 2,
                    backgroundColor: '#2196f3',
                    '&:hover': { backgroundColor: '#1976d2' },
                    borderRadius: '50px',
                    textTransform: 'none',
                    px: 3,
                  }}
                >
                  Options
                </Button>
                <Menu
                  anchorEl={anchorEl}
                  open={Boolean(anchorEl)}
                  onClose={handleDropdownClose}
                >
                  <MenuItem onClick={handleDropdownClose}>Placeholder Action</MenuItem>
                </Menu>
              </Box>
            </Grid>
          );
        })}
      </Grid>
    </Box>
  );
};

export default TaskCompletion;
