import React from 'react';
import { Box, Typography, CircularProgress, Button, Menu, MenuItem } from '@mui/material';

const TaskCompletion = ({ taskCompletions }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleDropdownOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleDropdownClose = () => {
    setAnchorEl(null);
  };

  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Task Completions
        </Typography>
        <Typography variant="body1">No task completion data available.</Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Task Completions by Area
      </Typography>
      {taskCompletions.map((area) => {
        const progress = (area.completed_count / area.total_tasks) * 100;

        return (
          <Box
            key={area.area_name}
            display="flex"
            alignItems="center"
            justifyContent="space-between"
            sx={{
              mb: 3,
              p: 2,
              border: '1px solid #ddd',
              borderRadius: '8px',
              backgroundColor: '#f9f9f9',
            }}
          >
            <Box display="flex" alignItems="center">
              <CircularProgress
                variant="determinate"
                value={progress}
                size={60}
                thickness={5}
                sx={{ color: '#673ab7', mr: 2 }}
              />
              <Box>
                <Typography variant="h6">{area.area_name}</Typography>
                <Typography variant="body2">
                  {area.completed_count} / {area.total_tasks} tasks completed
                </Typography>
              </Box>
            </Box>

            <Box>
              <Button
                variant="contained"
                color="primary"
                onClick={handleDropdownOpen}
                sx={{
                  backgroundColor: '#673ab7',
                  '&:hover': { backgroundColor: '#5e35b1' },
                  borderRadius: '50px',
                  textTransform: 'none',
                  px: 3,
                }}
              >
                Options
              </Button>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleDropdownClose}
              >
                <MenuItem onClick={handleDropdownClose}>Placeholder Action</MenuItem>
              </Menu>
            </Box>
          </Box>
        );
      })}
    </Box>
  );
};

export default TaskCompletion;
