import React from 'react';
import { Box, Typography, Grid } from '@mui/material';
import CircularProgress from '@mui/material/CircularProgress';

const TaskCompletion = ({ taskCompletions }) => {
  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Assignment Completion by Area
        </Typography>
        <Typography variant="body1">No assignment data available.</Typography>
      </Box>
    );
  }

  // Group tasks by area
  const areaGroups = {};

  taskCompletions.forEach((task) => {
    if (!areaGroups[task.area_name]) {
      areaGroups[task.area_name] = {
        name: task.area_name,
        completed: 0,
        total: 0,
      };
    }

    areaGroups[task.area_name].total++;
    if (task.is_correct) {
      areaGroups[task.area_name].completed++;
    }
  });

  const areas = Object.values(areaGroups);

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Assignment Completion by Area
      </Typography>
      <Grid container spacing={3}>
        {areas.map((area) => {
          const progress = area.total > 0 ? (area.completed / area.total) * 100 : 0;

          return (
            <Grid item xs={12} sm={6} md={4} key={area.name}>
              <Box
                sx={{
                  p: 3,
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  height: '200px',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                {/* Make sure CircularProgress is imported correctly */}
                <Box sx={{ position: 'relative', display: 'inline-flex' }}>
                  <CircularProgress
                    variant="determinate"
                    value={progress}
                    size={80}
                    thickness={4}
                    style={{ color: '#2196f3' }}
                  />
                  <Box
                    sx={{
                      top: 0,
                      left: 0,
                      bottom: 0,
                      right: 0,
                      position: 'absolute',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <Typography variant="h6" color="text.secondary">
                      {area.completed} / {area.total}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="h6" sx={{ mt: 2, color: '#1565c0' }}>
                  {area.name}
                </Typography>
              </Box>
            </Grid>
          );
        })}
      </Grid>
    </Box>
  );
};

export default TaskCompletion;
