const TaskAnalytics = ({ username }) => {
    // ... existing state and useEffect ...

    if (loading) return <CircularProgress />;

    // Add null checks for analyticsData and its properties
    if (!analyticsData || !analyticsData.tasksByArea || !analyticsData.difficultyDistribution) {
      return <Typography variant="body1">No analytics data available</Typography>;
    }

    return (
      <Grid container spacing={3} sx={{ mt: 2 }}>
        {/* Tasks per Area Chart */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Tasks Completed by Area
            </Typography>
            <BarChart width={500} height={300} data={analyticsData.tasksByArea}>
              {/* Add fallback for empty data */}
              {analyticsData.tasksByArea.length === 0 ? (
                <text x="50%" y="50%" textAnchor="middle">
                  No task data
                </text>
              ) : (
                <>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="area_name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="task_count" fill="#1976d2" name="Tasks Completed" />
                </>
              )}
            </BarChart>
          </Paper>
        </Grid>

        {/* Difficulty Distribution */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Difficulty Distribution
            </Typography>
            <PieChart width={500} height={300}>
              {analyticsData.difficultyDistribution.length === 0 ? (
                <text x="50%" y="50%" textAnchor="middle">
                  No difficulty data
                </text>
              ) : (
                <Pie
                  data={analyticsData.difficultyDistribution}
                  dataKey="count"
                  nameKey="difficulty_level"
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  label
                >
                  {analyticsData.difficultyDistribution.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={CHART_COLORS[index % CHART_COLORS.length]} />
                  ))}
                </Pie>
              )}
              <Tooltip />
              <Legend />
            </PieChart>
          </Paper>
        </Grid>
      </Grid>
    );
  };
