import React from 'react';
import { Box, Typography, CircularProgress, Button, Menu, MenuItem, Grid } from '@mui/material';

const TaskCompletion = ({ taskCompletions }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [selectedArea, setSelectedArea] = React.useState(null);

  const handleDropdownOpen = (event, area) => {
    setAnchorEl(event.currentTarget);
    setSelectedArea(area);
  };

  const handleDropdownClose = () => {
    setAnchorEl(null);
  };

  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Assignment Completion by Area
        </Typography>
        <Typography variant="body1">No assignment data available.</Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Assignment Completion by Area
      </Typography>
      <Grid container spacing={3}>
        {taskCompletions.map((area) => {
          // Calculate progress percentage
          const progress = (area.solved_tasks / area.total_tasks) * 100 || 0;

          return (
            <Grid item xs={12} sm={6} md={4} key={area.area_name}>
              <Box
                display="flex"
                flexDirection="column"
                alignItems="center"
                justifyContent="center"
                sx={{
                  p: 3,
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                  height: '200px',
                }}
              >
                <Box position="relative" display="inline-flex">
                  <CircularProgress
                    variant="determinate"
                    value={progress}
                    size={100}
                    thickness={5}
                    sx={{ color: '#2196f3' }}
                  />
                  <Box
                    position="absolute"
                    top="50%"
                    left="50%"
                    transform="translate(-50%, -50%)"
                  >
                    <Typography variant="h6" sx={{ color: '#1565c0' }}>
                      {area.solved_tasks} / {area.total_tasks}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="h6" sx={{ color: '#1565c0', textAlign: 'center', mt: 2 }}>
                  {area.area_name}
                </Typography>
                <Button
                  variant="contained"
                  onClick={(e) => handleDropdownOpen(e, area)}
                  sx={{
                    mt: 2,
                    backgroundColor: '#2196f3',
                    '&:hover': { backgroundColor: '#1976d2' },
                    borderRadius: '50px',
                    textTransform: 'none',
                    px: 3,
                  }}
                >
                  Options
                </Button>
              </Box>
            </Grid>
          );
        })}
      </Grid>
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleDropdownClose}
      >
        <MenuItem onClick={handleDropdownClose}>View Details</MenuItem>
        <MenuItem onClick={handleDropdownClose}>Mark as Complete</MenuItem>
      </Menu>
    </Box>
  );
};

export default TaskCompletion;
