import React from 'react';
import { Box, Typography, CircularProgress, Button, Menu, MenuItem, Grid } from '@mui/material';

const TaskCompletion = ({ taskCompletions }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [selectedAssignment, setSelectedAssignment] = React.useState(null);

  const handleDropdownOpen = (event, assignment) => {
    setAnchorEl(event.currentTarget);
    setSelectedAssignment(assignment);
  };

  const handleDropdownClose = () => {
    setAnchorEl(null);
  };

  if (!taskCompletions || taskCompletions.length === 0) {
    return (
      <Box>
        <Typography variant="h6" component="h2" gutterBottom>
          Assignments
        </Typography>
        <Typography variant="body1">No assignment data available.</Typography>
      </Box>
    );
  }

  // Group tasks by assignment
  const assignmentGroups = {};

  taskCompletions.forEach(task => {
    if (!assignmentGroups[task.statement_text]) {
      assignmentGroups[task.statement_text] = {
        name: task.statement_text,
        area: task.area_name,
        completed: 0,
        total: 0
      };
    }

    assignmentGroups[task.statement_text].total++;
    if (task.is_correct) {
      assignmentGroups[task.statement_text].completed++;
    }
  });

  const assignments = Object.values(assignmentGroups);

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Assignment Completion
      </Typography>
      <Grid container spacing={3}>
        {assignments.map((assignment) => {
          const progress = (assignment.completed / assignment.total) * 100;

          return (
            <Grid item xs={12} sm={6} md={4} key={assignment.name}>
              <Box
                display="flex"
                flexDirection="column"
                alignItems="center"
                justifyContent="center"
                sx={{
                  p: 3,
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#e3f2fd',
                  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                  height: '100%',
                }}
              >
                <CircularProgress
                  variant="determinate"
                  value={progress}
                  size={80}
                  thickness={5}
                  sx={{ color: '#2196f3', mb: 2 }}
                />
                <Typography variant="h6" sx={{ color: '#1565c0', textAlign: 'center' }}>
                  {assignment.name}
                </Typography>
                <Typography variant="body2" sx={{ color: '#1565c0', mb: 1 }}>
                  Area: {assignment.area}
                </Typography>
                <Typography variant="body2" sx={{ color: '#1565c0' }}>
                  {assignment.completed} / {assignment.total} tasks completed
                </Typography>
                <Button
                  variant="contained"
                  onClick={(e) => handleDropdownOpen(e, assignment)}
                  sx={{
                    mt: 2,
                    backgroundColor: '#2196f3',
                    '&:hover': { backgroundColor: '#1976d2' },
                    borderRadius: '50px',
                    textTransform: 'none',
                    px: 3,
                  }}
                >
                  Options
                </Button>
              </Box>
            </Grid>
          );
        })}
      </Grid>
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleDropdownClose}
      >
        <MenuItem onClick={handleDropdownClose}>View Details</MenuItem>
        <MenuItem onClick={handleDropdownClose}>Mark as Complete</MenuItem>
      </Menu>
    </Box>
  );
};

export default TaskCompletion;
